mapper.select:
SELECT id, date, proof_id, abstraction, subject_id, debit_money, credit_money
FROM sub_account


        AND subject_id=#{subjectId}


        AND date BETWEEN #{startDate} AND #{endDate}


ORDER BY date

    LIMIT #{offset},#{count}

SELECT COUNT(id) FROM sub_account


        AND subject_id=#{subjectId}


        AND date BETWEEN #{startDate} AND #{endDate}
